let app=new Vue({el:"#app",data:{filter:"",items:[]},methods:{search:function(){console.log(`Searching for ${this.filter}`);let n=this;fetch("js/index.json").then(function(t){if(t.status!==200){console.log("Looks like there was a problem. Status Code: "+t.status);return}t.json().then(function(t){let i=t.filter(t=>t.productName.toLowerCase().indexOf(n.filter.toLowerCase())!==-1),r=i.reduce((n,t)=>{let i=t.category.categoryName;return n[i]||(n[i]=[]),n[i].push(t),n},{});n.items=r})}).catch(function(n){console.log("Fetch Error :-S",n)})}}});